import React, { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import "./InputForm.css";

function InputForm({ onStudyGuideGenerated, studyGuide: loadedGuide }) {
  const [topic, setTopic] = useState("");
  const [error, setError] = useState("");
  const [studyGuide, setStudyGuide] = useState(loadedGuide || ""); // Load past guide if available
  const [displayedText, setDisplayedText] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (loadedGuide) {
      setStudyGuide(loadedGuide);
      setDisplayedText(""); // Clear displayed text and retype it
    }
  }, [loadedGuide]); // Run when a past study guide is selected

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setStudyGuide("");
    setDisplayedText("");
    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:8000/generate-guide", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_prompt: topic }),
      });

      if (response.ok) {
        const data = await response.json();
        setStudyGuide(data.study_guide);
        if (onStudyGuideGenerated) {
          onStudyGuideGenerated(data.study_guide);
        }
      } else {
        setError("Failed to generate study guide. Please try again.");
      }
    } catch (err) {
      console.error("Fetch error:", err);
      setError("Error communicating with the backend.");
    } finally {
      setIsLoading(false);
    }
  };

  // Typing effect logic for newly generated or loaded study guides
  useEffect(() => {
    if (studyGuide) {
      let index = 0;
      setDisplayedText(""); // Clear previous text before typing
      const interval = setInterval(() => {
        if (index < studyGuide.length) {
          setDisplayedText((prev) => prev + studyGuide[index]);
          index++;
        } else {
          clearInterval(interval);
        }
      }, 5);
      return () => clearInterval(interval);
    }
  }, [studyGuide]);

  return (
    <div className="input-form">
      <div className="study-guide-output">
        {isLoading ? (
          <div className="loading-container">
            <p className="placeholder-text">Generating your study guide...</p>
            <div className="loading-spinner"></div>
          </div>
        ) : displayedText ? (
          <ReactMarkdown>{displayedText}</ReactMarkdown>
        ) : (
          <>
            <p className="placeholder-text">Welcome to the Study Guide Generator.</p>
            <p className="description-text">
              Need help studying? Enter a topic, big or small, like 'The quadratic formula' 
              or 'Physics basics,' and we'll do the rest. The more specific you are, 
              the better we can help!
            </p>
          </>
        )}
      </div>

      <form className="prompt-bar" onSubmit={handleSubmit}>
        <input
          type="text"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          placeholder="Enter a topic..."
          required
        />
        <button type="submit">Generate</button>
        {error && <p className="error-message">{error}</p>}
      </form>
    </div>
  );
}

export default InputForm;
