import React, { useState, useEffect } from "react";
import "./Sidebar.css";

function Sidebar({ onNavigate, onSelectHistory, onLogout }) {
  const [history, setHistory] = useState([]);

  // Load history from local storage
  useEffect(() => {
    const savedHistory = JSON.parse(localStorage.getItem("studyGuideHistory")) || [];
    setHistory(savedHistory);
  }, []);

  return (
    <div className="sidebar">
      <h2 className="sidebar-title">TooturAI</h2>
      <ul className="nav-links-side">
        <li onClick={() => onNavigate("home")}>Home</li>
        <li onClick={() => onNavigate("studyGuide")}>Study Guides</li>
        <li onClick={() => onNavigate("flashcards")}>Flashcards</li>
        <li onClick={() => onNavigate("studyPlan")}>Study Plan</li>
        <li onClick={() => onNavigate("practiceTests")}>Practice Tests</li>
      </ul>

      {/* Study Guide History Section */}
      <div className="history-section">
        <h3 className="history-title">Previous Sessions</h3>
        <ul className="history-list">
          {history.length > 0 ? (
            history.map((item) => (
                <li key={item.id} onClick={() => onSelectHistory && onSelectHistory(item.studyGuide)}>
                {item.topic}
              </li>

            ))
          ) : (
            <p className="no-history">No previous sessions</p>
          )}
        </ul>
      </div>

      <li className="logout" onClick={onLogout}>Logout</li>
    </div>
  );
}

export default Sidebar;
