import React, { useEffect, useState } from "react";
import "./Sidebar.css";

function Sidebar({ onNavigate, onSelectHistory, onLogout, user, currentPage, history }) {
  const [studyGuides, setStudyGuides] = useState([]);
  {(history || []).map((guide, index) => (
    <li key={index} onClick={() => onSelectHistory(guide)}>
      {guide.title}
    </li>
  ))}
  // ðŸ†• Update sidebar **whenever history updates**
  useEffect(() => {
    if (history.length > 0) {
      setStudyGuides(history);
    }
  }, [history]); // React to history changes in real-time

  return (
    <div className="sidebar">
      <ul className="nav-links-side">
        <li onClick={() => {
          onSelectHistory(null);
          onNavigate("studyGuide");
        }}>
          Generate New Study Guide
        </li>

        <li onClick={() => onNavigate("flashcards")}>Flashcards</li>
        <li onClick={() => onNavigate("studyPlan")}>Study Plan</li>
        <li onClick={() => onNavigate("practiceTests")}>Practice Tests</li>
        <li className="logout" onClick={onLogout}>Logout</li>

        {/* Show previous sessions only on study guide page */}
        {studyGuides.length > 0 && currentPage === "studyGuide" && (
          <div className="history-section">
            <h3 className="history-title">Previous Sessions</h3>
            <ul className="history-list">
              {studyGuides.map((guide) => (
                <li key={guide._id} onClick={() => onSelectHistory(guide)}>
                  {guide.title}
                </li>
              ))}
            </ul>
          </div>
        )}
      </ul>
    </div>
  );
}

export default Sidebar;
