import React, { useState, useEffect } from "react";
import { GoogleOAuthProvider } from "@react-oauth/google";
import "./index";
import InputForm from "./components/InputForm.jsx";
import Login from "./components/login.js";
import Logout from "./components/logout.js";

const clientId = "881037752146-jhiu2q74gfkkj7phe2034p53m3nd5k6c.apps.googleusercontent.com";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null); // Holds user profile info
  const [studyGuide, setStudyGuide] = useState("");
  const [displayedText, setDisplayedText] = useState("");

  const handleStudyGuideGenerated = (generatedText) => {
    setStudyGuide(generatedText);
    setDisplayedText("");
  };

  useEffect(() => {
    if (studyGuide) {
      let index = 0;
      const interval = setInterval(() => {
        if (index < studyGuide.length) {
          setDisplayedText((prev) => prev + studyGuide[index]);
          index++;
        } else {
          clearInterval(interval);
        }
      }, 2);
      return () => clearInterval(interval);
    }
  }, [studyGuide]);

  const handleLoginSuccess = (credentialResponse) => {
    setIsAuthenticated(true);
    setUser(credentialResponse); // Save user data (e.g., profile info)
  };

  const handleLogoutSuccess = () => {
    setIsAuthenticated(false);
    setUser(null); // Clear user data
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div className="app">
        <nav className="navbar">
          <h1>TooturAI</h1>
          <div className="auth-buttons">
            {isAuthenticated ? (
              <Logout onLogoutSuccess={handleLogoutSuccess} />
            ) : (
              <Login onLoginSuccess={handleLoginSuccess} />
            )}
          </div>
        </nav>

        <div className="content">
          <div className="output-area">
            {studyGuide ? (
              <div className="study-guide">
                <h2>Generated Study Guide</h2>
                <p>{displayedText}</p>
              </div>
            ) : (
              <>
                <h1>Got an exam coming up?</h1>
                <h2>Enter a topic to generate a study guide.</h2>
              </>
            )}
          </div>
          <InputForm onStudyGuideGenerated={handleStudyGuideGenerated} />
        </div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
