import React, { useState } from "react";
import { GoogleOAuthProvider } from "@react-oauth/google";
import WelcomePage from "./components/WelcomePage";
import SignupForm from "./components/SignUpForm";
import HomePage from "./components/HomePage";
import InputForm from "./components/InputForm";
import Sidebar from "./components/Sidebar";
import TopNavbar from "./components/TopNavbar";

const clientId = "881037752146-jhiu2q74gfkkj7phe2034p53m3nd5k6c.apps.googleusercontent.com";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isProfileComplete, setIsProfileComplete] = useState(false);
  const [user, setUser] = useState(null);
  const [currentPage, setCurrentPage] = useState("home");
  const [errorMessage, setErrorMessage] = useState("");
  const [studyGuide, setStudyGuide] = useState("");

  const handleLoginSuccess = (user) => {
    setIsAuthenticated(true);
    setIsProfileComplete(true);
    setUser(user);
    setCurrentPage("home");
    setErrorMessage("");
  };

  const handleSignupSuccess = (user) => {
    setIsAuthenticated(true);
    setIsProfileComplete(false);
    setUser(user);
    setCurrentPage("signup");
    setErrorMessage("");
  };

  const handleProfileSubmit = async (profileData) => {
    const newUser = { ...user, ...profileData };

    try {
      const response = await fetch("http://localhost:8000/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
      });

      if (response.ok) {
        setIsProfileComplete(true);
        setCurrentPage("home");
      } else {
        setErrorMessage("Failed to save profile. Please try again.");
      }
    } catch (error) {
      console.error("Error saving profile:", error);
      setErrorMessage("Error saving profile. Please try again.");
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setIsProfileComplete(false);
    setUser(null);
    setCurrentPage("home");
    setErrorMessage("");
  };

  const handleStudyGuideGenerated = (generatedText) => {
    setStudyGuide(generatedText);
  };

  const renderPage = () => {
    if (!isAuthenticated) {
      return (
        <WelcomePage
          onLoginSuccess={handleLoginSuccess}
          onSignupSuccess={handleSignupSuccess}
          errorMessage={errorMessage}
          setErrorMessage={setErrorMessage}
        />
      );
    }

    if (!isProfileComplete) {
      return <SignupForm onSubmit={handleProfileSubmit} />;
    }

    switch (currentPage) {
      case "studyGuide":
        return <InputForm onStudyGuideGenerated={handleStudyGuideGenerated} />;
      case "home":
        return <HomePage onNavigate={setCurrentPage} />;
      default:
        return <HomePage onNavigate={setCurrentPage} />;
    }
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div className="app">
        {/* Top Navbar always renders */}
        <TopNavbar
          showAuthButtons={!isAuthenticated || !isProfileComplete} // Conditionally show Login/Signup buttons
        />
        {/* Sidebar only renders for authenticated users with complete profiles */}
        {isAuthenticated && isProfileComplete && (
          <Sidebar onNavigate={setCurrentPage} onLogout={handleLogout} />
        )}
        <div className={`main-content ${!isAuthenticated ? "centered" : ""}`}>
          {renderPage()}
        </div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
