import React, { useState } from "react";
import ReactMarkdown from "react-markdown";
import "./InputForm.css";

function InputForm({ onStudyGuideGenerated }) {
  const [topic, setTopic] = useState(""); // Topic entered by the user
  const [error, setError] = useState("");
  const [studyGuide, setStudyGuide] = useState(""); // Holds the generated study guide

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setStudyGuide(""); // Clear previous study guide

    try {
      const response = await fetch("http://localhost:8000/generate-guide", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_prompt: topic }),
      });

      if (response.ok) {
        const data = await response.json();
        setStudyGuide(data.study_guide); // Set the study guide locally
        if (onStudyGuideGenerated) {
          onStudyGuideGenerated(data.study_guide); // Notify parent (if necessary)
        }
      } else {
        setError("Failed to generate study guide. Please try again.");
      }
    } catch (err) {
      console.error("Fetch error:", err);
      setError("Error communicating with the backend.");
    }
  };

  return (
    <div className="input-form">
      <form className="prompt-bar" onSubmit={handleSubmit}>
        <input
          type="text"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          placeholder="Enter a topic..."
          required
        />
        <button type="submit">Generate</button>
        {error && <p className="error-message">{error}</p>}
      </form>

      {studyGuide && (
        <div className="study-guide-output">
          <h2>Generated Study Guide</h2>
          {/* Render the study guide with proper Markdown formatting */}
          <ReactMarkdown>{studyGuide}</ReactMarkdown>
        </div>
      )}
    </div>
  );
}

export default InputForm;
