import React, { useState } from "react";
import { GoogleOAuthProvider } from "@react-oauth/google";
import WelcomePage from "./components/WelcomePage";
import SignupForm from "./components/SignUpForm";
import HomePage from "./components/HomePage";
import InputForm from "./components/InputForm";
import Logout from "./components/logout";

const clientId = "881037752146-jhiu2q74gfkkj7phe2034p53m3nd5k6c.apps.googleusercontent.com";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isProfileComplete, setIsProfileComplete] = useState(false);
  const [isNewUser, setIsNewUser] = useState(false);
  const [user, setUser] = useState(null);
  const [currentPage, setCurrentPage] = useState("home");

  const handleLoginSuccess = async (credentialResponse) => {
    const userInfo = await fetch(
      "https://www.googleapis.com/oauth2/v3/userinfo",
      {
        headers: { Authorization: `Bearer ${credentialResponse.credential}` },
      }
    );
    const user = await userInfo.json();
    setUser(user);
    setIsAuthenticated(true);

    if (isNewUser) {
      setIsProfileComplete(false);
    } else {
      setIsProfileComplete(true);
    }
  };

  // const handleProfileSubmit = async (profileData) => {
  //   const newUser = { ...user, ...profileData };

  //   console.log("Payload being sent to backend:", newUser);

  //   try {
  //     const response = await fetch("http://localhost:8000/api/users", {
  //       method: "POST",
  //       headers: { "Content-Type": "application/json" },
  //       body: JSON.stringify(newUser),
  //     });

  //     if (response.ok) {
  //       setIsProfileComplete(true);
  //       console.log("Profile successfully created");
  //     } else {
  //       const errorResponse = await response.json();
  //       console.error("Failed to save profile to the backend:", errorResponse);
  //     }
  //   } catch (error) {
  //     console.error("Error saving profile:", error);
  //   }
  // };
  const handleProfileSubmit = async (profileData) => {
    const newUser = { ...user, ...profileData };

    console.log("Payload being sent to backend:", newUser);

    try {
      const response = await fetch("http://localhost:8000/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
      });

      const data = await response.json();

      if (response.ok && !data.exists) {
        setIsProfileComplete(true);
        console.log("Profile successfully created");
      } else if (data.exists) {
        alert("You already have an account with us. Please log in instead.");
      } else {
        console.error("Failed to save profile to the backend:", data);
      }
    } catch (error) {
      console.error("Error saving profile:", error);
    }
  };


  const handleLogout = () => {
    setIsAuthenticated(false);
    setIsProfileComplete(false);
    setUser(null);
    setCurrentPage("home");
  };

  const renderPage = () => {
    if (!isAuthenticated) {
      return (
        <WelcomePage
          onLogin={() => setIsNewUser(false)}
          onSignup={() => setIsNewUser(true)}
          onLoginSuccess={handleLoginSuccess}
        />
      );
    }

    if (!isProfileComplete && isNewUser) {
      return <SignupForm onSubmit={handleProfileSubmit} />;
    }

    switch (currentPage) {
      case "studyGuide":
        return <InputForm />;
      case "flashcards":
        return <div>Flashcards Component (To Be Implemented)</div>;
      case "studyPlan":
        return <div>Study Plan Component (To Be Implemented)</div>;
      case "practiceTests":
        return <div>Practice Tests Component (To Be Implemented)</div>;
      default:
        return <HomePage onNavigate={setCurrentPage} />;
    }
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div className="app">
        <nav className="navbar">
          <h1>TooturAI</h1>
          {isAuthenticated && <Logout onLogoutSuccess={handleLogout} />}
        </nav>
        <div className="content">{renderPage()}</div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
