import React from "react";
import { useGoogleLogin } from "@react-oauth/google";
import "./WelcomePage.css";
import styled from "styled-components";

const StyledHeader = styled.h1`
  font-family: "Sour Gummy", serif;
  font-weight: 700;
  font-style: normal;
  @import url('https://fonts.googleapis.com/css2?family=Sour+Gummy:ital,wght@0..900;1..900&display=swap');
`;
function WelcomePage({ onLoginSuccess, onSignupSuccess, errorMessage, setErrorMessage }) {
  const login = useGoogleLogin({
    onSuccess: async (response) => {
      try {
        // Fetch user profile from Google
        const userInfo = await fetch(
          "https://www.googleapis.com/oauth2/v3/userinfo",
          {
            headers: { Authorization: `Bearer ${response.access_token}` },
          }
        );
        const user = await userInfo.json();

        // Check if the user exists in the database
        const checkResponse = await fetch(
          `http://localhost:8000/api/users/check?email=${user.email}`
        );
        const checkData = await checkResponse.json();

        if (checkData.exists) {
          // Existing user: Log them in
          onLoginSuccess(user);
        } else {
          // Non-existing user: Show error
          setErrorMessage("You do not have an account yet. Please sign up.");
        }
      } catch (error) {
        console.error("Error during login process:", error);
        setErrorMessage("An error occurred during login. Please try again.");
      }
    },
    onError: (error) => {
      console.error("Login failed:", error);
      setErrorMessage("Login failed. Please try again.");
    },
  });

  const signup = useGoogleLogin({
    onSuccess: async (response) => {
      try {
        // Fetch user profile from Google
        const userInfo = await fetch(
          "https://www.googleapis.com/oauth2/v3/userinfo",
          {
            headers: { Authorization: `Bearer ${response.access_token}` },
          }
        );
        const user = await userInfo.json();

        // Check if the user exists in the database
        const checkResponse = await fetch(
          `http://localhost:8000/api/users/check?email=${user.email}`
        );
        const checkData = await checkResponse.json();

        if (checkData.exists) {
          // Existing user: Show error message
          setErrorMessage("You already have an account. Please log in instead.");
        } else {
          // New user: Redirect to the signup form
          onSignupSuccess(user);
        }
      } catch (error) {
        console.error("Error during signup process:", error);
        setErrorMessage("An error occurred during signup. Please try again.");
      }
    },
    onError: (error) => {
      console.error("Signup failed:", error);
      setErrorMessage("Signup failed. Please try again.");
    },
  });

  return (
    <div className="welcome-page">
      <h1 className="sour-gummy-ali">Welcome to TooturAI</h1>
      <p>Please log in or sign up to get started!</p>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <div className="buttons">
        <button onClick={() => login()}>Login</button>
        <button onClick={() => signup()}>Sign Up</button>
      </div>
    </div>
  );
}

export default WelcomePage;
