import React, { useState } from "react";
import { GoogleOAuthProvider } from "@react-oauth/google";
import WelcomePage from "./components/WelcomePage";
import SignupForm from "./components/SignUpForm";
import HomePage from "./components/HomePage";
import InputForm from "./components/InputForm";
import Logout from "./components/logout";

const clientId = "881037752146-jhiu2q74gfkkj7phe2034p53m3nd5k6c.apps.googleusercontent.com";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isProfileComplete, setIsProfileComplete] = useState(false);
  const [user, setUser] = useState(null);
  const [currentPage, setCurrentPage] = useState("welcome");
  const [errorMessage, setErrorMessage] = useState("");

  const clearError = () => setErrorMessage("");

  // const handleLoginSuccess = async (credentialResponse, isSignup = false) => {
  //   try {
  //     console.log("Credential Response:", credentialResponse); // Debug the entire response

  //     if (!credentialResponse || !credentialResponse.credential) {
  //       setErrorMessage("Failed to retrieve credential. Please try again.");
