  //     }

  //     const userInfo = await fetch(
  //       "https://www.googleapis.com/oauth2/v3/userinfo",
  //       {
  //         headers: { Authorization: `Bearer ${credentialResponse.credential}` },
  //       }
  //     );

  //     const user = await userInfo.json();
  //     console.log("User Info:", user); // Debugging log for user info

  //     if (!user.email) {
  //       setErrorMessage("Failed to retrieve user email. Please try again.");
  //       return;
  //     }

  //     // Handle user existence check and page redirection
  //     const response = await fetch(`http://localhost:8000/api/users/check?email=${user.email}`);
  //     const data = await response.json();

  //     if (data.exists && isSignup) {
  //       setErrorMessage("You already have an account with us. Please log in instead.");
  //       return;
  //     }

  //     if (!data.exists && !isSignup) {
  //       setErrorMessage("Account not found. Please sign up instead.");
  //       return;
  //     }

  //     if (data.exists) {
  //       setUser(user);
  //       setIsAuthenticated(true);
  //       setIsProfileComplete(true);
  //       setCurrentPage("home");
  //     } else if (isSignup) {
  //       setUser(user);
  //       setIsAuthenticated(true);
  //       setIsProfileComplete(false);
  //       setCurrentPage("signup");
  //     }
  //   } catch (error) {
  //     console.error("Error during login/signup process:", error);
  //     setErrorMessage("An error occurred. Please try again.");
  //   }
  // };
  const handleLoginSuccess = async (credentialResponse, isSignup = false) => {
    try {
      console.log("Credential Response:", credentialResponse); // Debugging log

      // Decode the credentialResponse token to get user information
      const base64Url = credentialResponse.credential.split(".")[1];
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const jsonPayload = decodeURIComponent(
        atob(base64)
          .split("")
          .map(function (c) {
            return "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2);
          })
          .join("")
      );

      const user = JSON.parse(jsonPayload);
      console.log("Decoded User Info:", user); // Debug decoded user info

      if (!user.email) {
        setErrorMessage("Failed to retrieve user email. Please try again.");
        return;
      }

      // Check if the user exists in the database
      const response = await fetch(`http://localhost:8000/api/users/check?email=${user.email}`);
      const data = await response.json();

      if (data.exists && isSignup) {
        // User exists but is trying to sign up
        setErrorMessage("You already have an account with us. Please log in instead.");
        return;
      }

      if (!data.exists && !isSignup) {
        // User does not exist but is trying to log in
        setErrorMessage("Account not found. Please sign up instead.");
        return;
      }

      // Valid cases
      if (data.exists) {
        // Existing user logs in
        setUser(user);
        setIsAuthenticated(true);
        setIsProfileComplete(true);
        setCurrentPage("home"); // Redirect to home page
      } else if (isSignup) {
        // New user signing up
        setUser(user);
        setIsAuthenticated(true);
        setIsProfileComplete(false);
        setCurrentPage("signup"); // Redirect to profile creation page
      }
    } catch (error) {
      console.error("Error during login/signup process:", error);
      setErrorMessage("An error occurred. Please try again.");
    }
  };



  const handleProfileSubmit = async (profileData) => {
    const newUser = { ...user, ...profileData };

    try {
      const response = await fetch("http://localhost:8000/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
      });

      if (response.ok) {
        setIsProfileComplete(true);
        setCurrentPage("home");
      } else {
        setErrorMessage("Failed to save profile. Please try again.");
      }
    } catch (error) {
      console.error("Error saving profile:", error);
      setErrorMessage("Error saving profile. Please try again.");
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setIsProfileComplete(false);
    setUser(null);
    setCurrentPage("welcome");
  };

  const renderPage = () => {
    switch (currentPage) {
      case "welcome":
        return (
          <WelcomePage
            onLogin={(credentialResponse) => handleLoginSuccess(credentialResponse, false)}
            onSignup={(credentialResponse) => handleLoginSuccess(credentialResponse, true)}
            errorMessage={errorMessage}
            clearError={clearError}
          />
        );
      case "signup":
        return <SignupForm onSubmit={handleProfileSubmit} />;
      case "home":
        return <HomePage onNavigate={setCurrentPage} />;
      default:
        return <WelcomePage />;
    }
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div className="app">
        <nav className="navbar">
          <h1>TooturAI</h1>
          {isAuthenticated && <Logout onLogoutSuccess={handleLogout} />}
        </nav>
        <div className="content">{renderPage()}</div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
