import React, { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import "./InputForm.css";

function InputForm({ onStudyGuideGenerated, user, selectedStudyGuide, setSelectedStudyGuide }) {
  const [topic, setTopic] = useState("");
  const [error, setError] = useState("");
  const [conversation, setConversation] = useState([]); // Store chat history
  const [isLoading, setIsLoading] = useState(false);

  // Load conversation when a study guide is selected
  useEffect(() => {
    if (selectedStudyGuide) {
      setConversation(selectedStudyGuide.conversation);
    } else {
      setConversation([]); // Reset if no guide is selected
    }
  }, [selectedStudyGuide]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:8000/api/generate-guide", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          email: user.email,
          user_prompt: topic,
          study_guide_id: selectedStudyGuide ? selectedStudyGuide._id : null,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        const newResponse = { user_prompt: topic, response: data.message };

        // Update local conversation
        setConversation((prev) => [...prev, newResponse]);

        if (!selectedStudyGuide) {
          setSelectedStudyGuide({ _id: data.study_guide_id, title: topic, conversation: [newResponse] });
          onStudyGuideGenerated(data.message, topic);
        }
      } else {
        setError("Failed to generate study guide. Please try again.");
      }
    } catch (err) {
      console.error("Fetch error:", err);
      setError("Error communicating with the backend.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="input-form">
      <div className="study-guide-output">
        {conversation.map((msg, index) => (
          <div key={index} className={msg.user_prompt ? "user-message" : "ai-message"}>
            <ReactMarkdown>{msg.user_prompt || msg.response}</ReactMarkdown>
          </div>
        ))}
      </div>

      <form className="prompt-bar" onSubmit={handleSubmit}>
        <input
          type="text"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          placeholder="Ask a follow-up question..."
          required
        />
        <button type="submit">Send</button>
        {error && <p className="error-message">{error}</p>}
      </form>
    </div>
  );
}

export default InputForm;
