import React, { useEffect, useState } from "react";
import "./Sidebar.css";

function Sidebar({ onNavigate, user }) {
  const [studyGuides, setStudyGuides] = useState([]);

  useEffect(() => {
    if (user) {
      fetch(`http://localhost:8000/get-study-guides?email=${user.email}`)
        .then((response) => response.json())
        .then((data) => {
          setStudyGuides(data.study_guides || []);
        })
        .catch((error) => console.error("Error fetching study guides:", error));
    }
  }, [user]);

  return (
    <div className="sidebar">
      <h2 className="sidebar-title">TooturAI</h2>
      <ul className="nav-links">
        <li onClick={() => onNavigate("home")}>Home</li>
        <li onClick={() => onNavigate("studyGuide")}>New Study Guide</li>

        {/* Display past study guides */}
        {studyGuides.length > 0 && (
          <div className="history-section">
            <h3 className="history-title">Previous Sessions</h3>
            <ul className="history-list">
              {studyGuides.map((guide) => (
                <li key={guide._id} onClick={() => onNavigate("studyGuide", guide)}>
                  {guide.title}
                </li>
              ))}
            </ul>
          </div>
        )}

        <li onClick={() => onNavigate("flashcards")}>Flashcards</li>
        <li onClick={() => onNavigate("studyPlan")}>Study Plan</li>
        <li onClick={() => onNavigate("practiceTests")}>Practice Tests</li>
        <li className="logout" onClick={() => onNavigate("logout")}>Logout</li>
      </ul>
    </div>
  );
}

export default Sidebar;
