import React, { useState } from "react";
import { GoogleOAuthProvider } from "@react-oauth/google";
import WelcomePage from "./components/WelcomePage";
import SignupForm from "./components/SignupForm";
import InputForm from "./components/InputForm";
import Logout from "./components/logout";

const clientId = "881037752146-jhiu2q74gfkkj7phe2034p53m3nd5k6c.apps.googleusercontent.com";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isProfileComplete, setIsProfileComplete] = useState(false);
  const [isNewUser, setIsNewUser] = useState(false); // Tracks if the user is signing up
  const [user, setUser] = useState(null);

  const handleLoginSuccess = async (credentialResponse) => {
    const userInfo = await fetch(
      "https://www.googleapis.com/oauth2/v3/userinfo",
      {
        headers: { Authorization: `Bearer ${credentialResponse.credential}` },
      }
    );
    const user = await userInfo.json();
    setUser(user);
    setIsAuthenticated(true);

    if (isNewUser) {
      // If the user is signing up, prompt profile creation
      setIsProfileComplete(false);
    } else {
      // If the user is logging in, skip profile creation
      setIsProfileComplete(true);
    }
  };

  const handleProfileSubmit = async (profileData) => {
    const newUser = { ...user, ...profileData };

    try {
      const response = await fetch("http://localhost:8000/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
      });

      if (response.ok) {
        setIsProfileComplete(true);
      } else {
        console.error("Failed to save profile to the backend");
      }
    } catch (error) {
      console.error("Error saving profile:", error);
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setIsProfileComplete(false);
    setUser(null);
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div className="app">
        <nav className="navbar">
          <h1>TooturAI</h1>
          {isAuthenticated && <Logout onLogoutSuccess={handleLogout} />}
        </nav>

        <div className="content">
          {!isAuthenticated ? (
            // Show Welcome Page when not authenticated
            <WelcomePage
              onLogin={() => setIsNewUser(false)} // Existing users
              onSignup={() => setIsNewUser(true)} // New users
              onLoginSuccess={handleLoginSuccess}
            />
          ) : !isProfileComplete && isNewUser ? (
            // Show Profile Creation form for new users
            <SignupForm onSubmit={handleProfileSubmit} />
          ) : (
            // Show the Study Guide Generator for authenticated users
            <InputForm />
          )}
        </div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
