import React, { useState } from "react";
import "./index";
import InputForm from "./components/InputForm.jsx";

function App() {
  const [studyGuide, setStudyGuide] = useState("");

  const handleStudyGuideGenerated = (generatedText) => {
    setStudyGuide(generatedText);
  };

  return (
    <div className="app">
      <Navbar />
      <div className="content">
        <div className="output-area">
          {studyGuide ? (
            <div className="study-guide">
              <h2>Generated Study Guide</h2>
              <p>{studyGuide}</p>
            </div>
          ) : (
            <p className="placeholder">Your generated study guide will appear here.</p>
          )}
        </div>
        <InputForm onStudyGuideGenerated={handleStudyGuideGenerated} />
      </div>
    </div>
  );
}

export default App;
