import React, { useState } from "react";
import ReactMarkdown from "react-markdown";
import "./InputForm.css";

function InputForm({ onStudyGuideGenerated, user, isSidebarOpen, setIsSidebarOpen }) {
  const [topic, setTopic] = useState("");
  const [error, setError] = useState("");
  const [studyGuide, setStudyGuide] = useState("");
  const [displayedText, setDisplayedText] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isHovered, setIsHovered] = useState(false); // ðŸ”¹ Track hover state

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setStudyGuide("");
    setDisplayedText("");
    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:8000/generate-guide", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ user_prompt: topic }),
      });

      if (response.ok) {
        const data = await response.json();
        setStudyGuide(data.study_guide);
        onStudyGuideGenerated(data.study_guide);
      } else {
        setError("Failed to generate study guide. Please try again.");
      }
    } catch (err) {
      setError("Error communicating with the backend.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="input-form">
      {/* Sidebar Toggle Button (â˜°) */}
      <button
        className="sidebar-toggle"
        onClick={() => setIsSidebarOpen(!isSidebarOpen)}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        â˜°
        {isHovered && <span className="tooltip">Open Sidebar</span>}
      </button>

      <div className="study-guide-output">
        {isLoading ? (
          <div className="loading-container">
            <p className="placeholder-text">Generating your study guide...</p>
            <div className="loading-spinner"></div>
          </div>
        ) : displayedText ? (
          <ReactMarkdown>{displayedText}</ReactMarkdown>
        ) : (
          <>
            <p className="placeholder-text">Welcome to the Study Guide Generator.</p>
            <p className="description-text">
              Need help studying? Enter a topic, big or small, like 'The quadratic formula' 
              or 'Physics basics,' and we'll do the rest.
            </p>
          </>
        )}
      </div>

      <form className="prompt-bar" onSubmit={handleSubmit}>
        <input
          type="text"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          placeholder="Enter a topic..."
          required
        />
        <button type="submit">Generate</button>
        {error && <p className="error-message">{error}</p>}
      </form>
    </div>
  );
}

export default InputForm;
