import React, { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import "./InputForm.css";

function InputForm({ onStudyGuideGenerated }) {
  const [topic, setTopic] = useState(""); // User input
  const [error, setError] = useState(""); // Error messages
  const [studyGuide, setStudyGuide] = useState(""); // Full study guide
  const [displayedText, setDisplayedText] = useState(""); // Typing effect text
  const [isLoading, setIsLoading] = useState(false); // Loading state

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setStudyGuide(""); // Clear the study guide on new request
    setDisplayedText(""); // Clear the displayed text
    setIsLoading(true); // Show loading placeholder

    try {
      const response = await fetch("http://localhost:8000/generate-guide", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_prompt: topic }),
      });

      if (response.ok) {
        const data = await response.json();
        setStudyGuide(data.study_guide); // Full text is set here
        if (onStudyGuideGenerated) {
          onStudyGuideGenerated(data.study_guide); // Notify parent if needed
        }
      } else {
        setError("Failed to generate study guide. Please try again.");
      }
    } catch (err) {
      console.error("Fetch error:", err);
      setError("Error communicating with the backend.");
    } finally {
      setIsLoading(false); // Stop showing loading placeholder
    }
  };

  // Typing effect logic
  useEffect(() => {
    if (studyGuide) {
      let index = 0;
      const interval = setInterval(() => {
        if (index < studyGuide.length) {
          setDisplayedText((prev) => prev + studyGuide[index]);
          index++;
        } else {
          clearInterval(interval); // Stop the typing effect when done
        }
      }, 5); // Adjust typing speed here (ms per character)

      return () => clearInterval(interval); // Clean up on unmount
    }
  }, [studyGuide]);

  return (
    <div className="input-form">
      <div className="study-guide-output">
        {isLoading ? (
          <div className="loading-container">
          <p className="placeholder-text">Generating your study guide...</p>
          <div className="loading-spinner"></div>
          </div>
        ) : displayedText ? (
          <ReactMarkdown>{displayedText}</ReactMarkdown>
        ) : (
          <>
            <p className="placeholder-text">
              Welcome to the Study Guide Generator.
            </p>
            <p className="description-text">
            Need help studying? Enter a topic, big or small, like 'The quadratic formula' or 'Physics basics,' and we'll do the rest. The more specific you are, the better we can help!
            </p>
          </>
        )}
      </div>

      <form className="prompt-bar" onSubmit={handleSubmit}>
        <input
          type="text"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          placeholder="Enter a topic..."
          required
        />
        <button type="submit">Generate</button>
        {error && <p className="error-message">{error}</p>}
      </form>
    </div>
  );
}

export default InputForm;
