import React, { useState } from "react";

function InputForm() {
  const [topic, setTopic] = useState("");
  const [studyGuide, setStudyGuide] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Reset error message

    try {
      const response = await fetch("http://localhost:8000/generate-guide", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_prompt: topic }),
      });

      if (response.ok) {
        const data = await response.json();
        setStudyGuide(data.study_guide);
      } else {
        setError("Failed to generate study guide. Please try again.");
      }
    } catch (err) {
      setError("Error communicating with the backend.");
      console.error(err);
    }
  };

  return (
    <div style={formContainerStyles}>
      <form onSubmit={handleSubmit} style={formStyles}>
        <input
          type="text"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          placeholder="Enter a topic"
          style={inputStyles}
          required
        />
        <button type="submit" style={buttonStyles}>
          Generate
        </button>
      </form>

      {error && <p style={errorStyles}>{error}</p>}

      {studyGuide && (
        <div style={resultStyles}>
          <h2>Generated Study Guide</h2>
          <p>{studyGuide}</p>
        </div>
      )}
    </div>
  );
}

const formContainerStyles = {
  textAlign: "center",
  marginTop: "50px",
};

const formStyles = {
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  gap: "10px",
};

const inputStyles = {
  padding: "10px",
  fontSize: "16px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  width: "300px",
};

const buttonStyles = {
  padding: "10px 20px",
  fontSize: "16px",
  color: "white",
  backgroundColor: "#4CAF50",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
};

const errorStyles = {
  color: "red",
  marginTop: "10px",
};

const resultStyles = {
  marginTop: "20px",
  textAlign: "left",
  maxWidth: "600px",
  margin: "0 auto",
  background: "#f9f9f9",
  padding: "15px",
  borderRadius: "5px",
  boxShadow: "0 2px 4px rgba(0, 0, 0, 0.2)",
};

export default InputForm;
