import React, { useState } from "react";
import { GoogleOAuthProvider } from "@react-oauth/google";
import WelcomePage from "./components/WelcomePage";
import SignupForm from "./components/SignUpForm";
import HomePage from "./components/HomePage";
import InputForm from "./components/InputForm";
import Logout from "./components/logout";

const clientId = "881037752146-jhiu2q74gfkkj7phe2034p53m3nd5k6c.apps.googleusercontent.com";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isProfileComplete, setIsProfileComplete] = useState(false);
  const [isNewUser, setIsNewUser] = useState(false);
  const [user, setUser] = useState(null);
  const [currentPage, setCurrentPage] = useState("welcome");
  const [errorMessage, setErrorMessage] = useState("");

  const clearError = () => setErrorMessage("");

  const handleLoginSuccess = async (credentialResponse, isSignup = false) => {
    try {
      // Fetch user info from Google
      const userInfo = await fetch(
        "https://www.googleapis.com/oauth2/v3/userinfo",
        {
          headers: { Authorization: `Bearer ${credentialResponse.credential}` },
        }
      );
      const user = await userInfo.json();

      // Check if the user exists in the database
      const response = await fetch(`http://localhost:8000/api/users/check?email=${user.email}`);
      const data = await response.json();

      if (data.exists && isSignup) {
        // User exists and is trying to sign up
        setErrorMessage("You already have an account with us. Please log in instead.");
        return;
      }

      if (!data.exists && !isSignup) {
        // User does not exist and is trying to log in
        setErrorMessage("Account not found. Please sign up instead.");
        return;
      }

      // Handle valid cases
      if (data.exists) {
        // Existing user logs in successfully
        setUser(user);
        setIsAuthenticated(true);
        setIsProfileComplete(true);
        setCurrentPage("home"); // Redirect to home page
      } else if (isSignup) {
        // New user signing up
        setUser(user);
        setIsAuthenticated(true);
        setIsProfileComplete(false);
        setCurrentPage("signup"); // Redirect to profile creation
      }
    } catch (error) {
      console.error("Error during login/signup process:", error);
      setErrorMessage("An error occurred. Please try again.");
    }
  };
  const handleProfileSubmit = async (profileData) => {
    const newUser = { ...user, ...profileData };

    try {
      const response = await fetch("http://localhost:8000/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
      });

      if (response.ok) {
        setIsProfileComplete(true);
        setCurrentPage("home"); // Redirect to home page after profile creation
      } else {
        setErrorMessage("Failed to save profile. Please try again.");
      }
    } catch (error) {
      console.error("Error saving profile:", error);
      setErrorMessage("Error saving profile. Please try again.");
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setIsProfileComplete(false);
    setIsNewUser(false);
    setUser(null);
    setCurrentPage("welcome");
  };

  const renderPage = () => {
    switch (currentPage) {
      case "welcome":
        return (
          <WelcomePage
            onLogin={(credentialResponse) => handleLoginSuccess(credentialResponse, false)}
            onSignup={(credentialResponse) => handleLoginSuccess(credentialResponse, true)}
            errorMessage={errorMessage}
            clearError={clearError}
          />
        );
      case "signup":
        return <SignupForm onSubmit={handleProfileSubmit} />;
      case "home":
        return <HomePage onNavigate={setCurrentPage} />;
      case "studyGuide":
        return <InputForm />;
      case "flashcards":
        return <div>Flashcards Component (To Be Implemented)</div>;
      case "studyPlan":
        return <div>Study Plan Component (To Be Implemented)</div>;
      case "practiceTests":
        return <div>Practice Tests Component (To Be Implemented)</div>;
      default:
        return <WelcomePage />;
    }
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div className="app">
        <nav className="navbar">
          <h1>TooturAI</h1>
          {isAuthenticated && <Logout onLogoutSuccess={handleLogout} />}
        </nav>
        <div className="content">{renderPage()}</div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
