import React, { useState } from "react";
import "./App.css";

function App() {
  const [topic, setTopic] = useState("");
  const [studyGuide, setStudyGuide] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await fetch("http://localhost:8000/generate-guide", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_prompt: topic }),
      });

      if (response.ok) {
        const data = await response.json();
        setStudyGuide(data.study_guide);
      } else {
        setError("Failed to generate study guide. Please try again.");
      }
    } catch (err) {
      setError("Error communicating with the backend.");
      console.error(err);
    }
  };

  return (
    <div className="app">
      <nav className="navbar">
        <h1>TooturAI</h1>
      </nav>
      <div className="content">
        <div className="output-area">
          {studyGuide ? (
            <div className="study-guide">
              <h2>Generated Study Guide</h2>
              <p>{studyGuide}</p>
            </div>
          ) : (
            <p className="placeholder">Your generated study guide will appear here.</p>
          )}
        </div>
        <form className="prompt-bar" onSubmit={handleSubmit}>
          <input
            type="text"
            value={topic}
            onChange={(e) => setTopic(e.target.value)}
            placeholder="Enter a topic..."
            required
          />
          <button type="submit">Generate</button>
        </form>
      </div>
    </div>
  );
}

export default App;
