import React, { useState } from "react";
import { GoogleOAuthProvider } from "@react-oauth/google";
import WelcomePage from "./components/WelcomePage";
import SignupForm from "./components/SignUpForm";
import InputForm from "./components/InputForm";
import Login from "./components/login";
import Logout from "./components/logout";

const clientId = "881037752146-jhiu2q74gfkkj7phe2034p53m3nd5k6c.apps.googleusercontent.com";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isProfileComplete, setIsProfileComplete] = useState(false);
  const [user, setUser] = useState(null);
  const [studyGuide, setStudyGuide] = useState("");

  const handleLoginSuccess = (credentialResponse) => {
    setIsAuthenticated(true);
    setUser(credentialResponse); // Save user data
  };

  const handleSignupSubmit = async (profileData) => {
    // Combine user data and profile info
    const newUser = { ...user, ...profileData };

    // Send profile data to the backend
    try {
      const response = await fetch("http://localhost:8000/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
      });

      if (response.ok) {
        setIsProfileComplete(true);
        console.log("Profile successfully created");
      } else {
        console.error("Failed to save profile to the backend");
      }
    } catch (error) {
      console.error("Error saving profile:", error);
    }
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div className="app">
        <nav className="navbar">
          <h1>TooturAI</h1>
          <div className="auth-buttons">
            {isAuthenticated ? (
              <Logout onLogoutSuccess={() => setIsAuthenticated(false)} />
            ) : (
              <Login onLoginSuccess={handleLoginSuccess} />
            )}
          </div>
        </nav>

        <div className="content">
          {!isAuthenticated ? (
            <WelcomePage
              onLogin={() => setIsAuthenticated(true)}
              onSignup={() => setIsProfileComplete(false)}
            />
          ) : !isProfileComplete ? (
            <SignupForm onSubmit={handleSignupSubmit} />
          ) : (
            <>
              <h2>Welcome, {user.name}!</h2>
              <InputForm />
            </>
          )}
        </div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
